import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { ButtonToolbar, Button, ToggleButton, Collapse, ToggleButtonGroup, Container } from 'react-bootstrap/'
import { useState } from 'react';
import { Icon } from 'semantic-ui-react';

function Expand({ onEnvToggle, environmentalFactors, onCoatChange }) {
    const [open, setOpen] = useState(false);
    const { cats, dogs, children } = environmentalFactors;
    return (
        <Container>
            <Button
                variant="secondary" size="medium"
                onClick={() => setOpen(!open)}
                aria-controls="example-collapse-text"
                aria-expanded={open}
            >
                More filters <Icon name={open ? 'chevron up' : 'chevron down'} />
            </Button>
            <Collapse in={open} >
                <div className='mt-3'>
                    Coat
                    <div className="d-flex" style={{ gap: '0.5rem' }}>
                        <Button variant={cats ? 'primary' : 'secondary'} onClick={() => onCoatChange('hairless')}>Hairless</Button>
                        <Button variant={dogs ? 'primary' : 'secondary'} onClick={() => onCoatChange('short')}>Short</Button>
                        <Button variant={children ? 'primary' : 'secondary'} onClick={() => onCoatChange('long')}>Long</Button>
                        <Button variant={children ? 'primary' : 'secondary'} onClick={() => onCoatChange('wire')}>Wire</Button>
                        <Button variant={children ? 'primary' : 'secondary'} onClick={() => onCoatChange('curly')}>Curly</Button>

                    </div>

                    <div className="fw-bold">Good With</div>
                    <ButtonToolbar className="d-flex" style={{ gap: '0.5rem' }}>
                        <Button variant={cats ? 'primary' : 'secondary'} onClick={() => onEnvToggle('good_with_cats')}>Cats</Button>
                        <Button variant={dogs ? 'primary' : 'secondary'} onClick={() => onEnvToggle('good_with_dogs')}>Dogs</Button>
                        <Button variant={children ? 'primary' : 'secondary'} onClick={() => onEnvToggle('good_with_children')}>Children</Button>
                    </ButtonToolbar>

                </div>
            </Collapse>
        </Container >
    );
}

export default Expand;
