import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { ButtonToolbar, Button, ToggleButton, Collapse, ToggleButtonGroup, Container } from 'react-bootstrap/'
import { useState } from 'react';

function Expand({ onEnvToggle, environmentalFactors }) {
    const [open, setOpen] = useState(false);
    const { cats, dogs, children } = environmentalFactors;
    return (
        <Container>
            <Button
                variant="secondary" size="lg"
                onClick={() => setOpen(!open)}
                aria-controls="example-collapse-text"
                aria-expanded={open}
            >
                More
            </Button>
            <Collapse in={open}>
                <div>

                    Coat
                    <ButtonToolbar>
                        <ToggleButtonGroup type="checkbox" >
                            <ToggleButton value={1}>Hairless</ToggleButton>
                            <ToggleButton value={2}>Short </ToggleButton>
                            <ToggleButton value={3}>Long</ToggleButton>
                            <ToggleButton value={4}>Wire</ToggleButton>
                            <ToggleButton value={4}>Curly</ToggleButton>
                        </ToggleButtonGroup>
                    </ButtonToolbar>

                    <ButtonToolbar>
                        <div className="fw-bold">Good With</div>
                        <ButtonToolbar className="d-flex gap-2">
                            <Button variant={cats ? 'primary' : 'secondary'} onClick={() => onEnvToggle('good_with_cats')}>Cats</Button>
                            <Button variant={dogs ? 'primary' : 'secondary'} onClick={() => onEnvToggle('good_with_dogs')}>Dogs</Button>
                            <Button variant={children ? 'primary' : 'secondary'} onClick={() => onEnvToggle('good_with_children')}>Children</Button>
                        </ButtonToolbar>
                    </ButtonToolbar>
                </div>
            </Collapse>
        </Container>
    );
}

export default Expand;
