import React, { useState, useEffect, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, ButtonToolbar, ToggleButton, ToggleButtonGroup } from 'react-bootstrap';
import { Icon, Input, Button, Form, Pagination } from 'semantic-ui-react';
import { Grid, Rail, Ref, Segment, Sticky } from 'semantic-ui-react';
import GetData from './GetData.js';
import { boundChangePetData } from '../Redux/actions';

const domain = "http://localhost:5000"; // your running proxy

const tokenUrl = '/v2/oauth2/token';
const url = `/v2/animals`;

const clientId = "z75x8asWGx4VqYVYbTjjGmrbGKCyd8euVj0YJxQPDSXT5lfnvv";
const clientSecret = "CurSmxCxNgci2vHdbeZlPYi3hlAsnxZyaaZNh3Ny";

const SearchTool = () => {
    const contextRef = useRef();
    const tokenRef = useRef("");

    const [state, setState] = useState({
        petType: 'Dog',
        page: 1,
        location: '94112',
        sort: '-recent',
        coat: '',
        age: '',
        good_with_children: '',
        good_with_dogs: '',
        good_with_cats: '',
        input: '',
        limit: '20',
        totalPageCount: '',
        currentPage: '',
        isloading: null,
        loadingMessage: 'loading...',
        items: [],
        isLoaded: false,
        empty: ''
    });

    const getToken = async () => {
        const res = await fetch(`${domain}${tokenUrl}`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
            body: `grant_type=client_credentials&client_id=${clientId}&client_secret=${clientSecret}`
        });
        const data = await res.json();
        tokenRef.current = data.access_token;
    };

    const requestData = async () => {
        const params = { ...state };
        let queryString = Object.entries(params)
            .filter(([_, val]) => val !== '' && val !== null)
            .map(([key, val]) => `${key}=${val}`)
            .join('&');

        const res = await fetch(`${domain}${url}?${queryString}`, {
            headers: {
                Authorization: `Bearer ${tokenRef.current}`,
                'Content-Type': 'application/json'
            }
        });
        const data = await res.json();

        if (!data.animals.length) {
            setState(prev => ({ ...prev, empty: 'Sorry! No animals found in your area.' }));
        }

        boundChangePetData(data.animals);
        setState(prev => ({
            ...prev,
            isLoaded: true,
            items: data.animals,
            totalPageCount: data.pagination?.total_pages,
            currentPage: data.pagination?.current_page
        }));
    };

    useEffect(() => {
        (async () => {
            await getToken();
            await requestData();
        })();
    }, []);

    const handleInputChange = (e) => setState(prev => ({ ...prev, input: e.target.value }));

    const handleSubmitSearchCity = async (e) => {
        e.preventDefault();
        setState(prev => ({ ...prev, location: prev.input, input: '' }));
        await getToken();
        await requestData();
    };

    const handleToggleType = async (val) => {
        setState(prev => ({ ...prev, petType: val }));
        await getToken();
        await requestData();
    };

    const handlePageChange = async (e, data) => {
        setState(prev => ({ ...prev, page: data.activePage, isloading: true }));
        await getToken();
        await requestData();
        setState(prev => ({ ...prev, isloading: false }));
    };

    return (
        <Container className="dataBox">
            <Grid>
                <Grid.Column columns={2}>
                    <Ref innerRef={contextRef}>
                        <Container>
                            <Container className="displayData mt-auto p-2">
                                <Sticky className="stickyDirectionBar d-flex justfiy-content-center">
                                    <Pagination
                                        onPageChange={handlePageChange}
                                        defaultActivePage={state.page}
                                        totalPages={state.totalPageCount}
                                    />
                                </Sticky>
                                <div>{state.empty}</div>
                                <GetData className="dataBox displayData mt-auto p-2" items={state.items} isLoaded={state.isLoaded} />
                            </Container>
                            <Rail>
                                <Sticky className="stickySearchBar">
                                    <Segment className="positionSticky">
                                        <Form onSubmit={handleSubmitSearchCity}>
                                            <Form.Field type="submit">
                                                Zip code
                                                <Input
                                                    placeholder='94112....'
                                                    value={state.input}
                                                    onChange={handleInputChange}
                                                    icon={<Icon name='search' inverted circular link onClick={handleSubmitSearchCity} />}
                                                />
                                            </Form.Field>
                                        </Form>
                                        <div> Pick your favorite furry </div>
                                        <Button.Group>
                                            <ToggleButtonGroup type="radio" name="options" onChange={handleToggleType}>
                                                <div> Species </div>
                                                <ToggleButton variant="secondary" size='lg' value={'dog'}>Dog</ToggleButton>
                                                <Button.Or />
                                                <ToggleButton variant="secondary" size='lg' value={'cat'}>Cat</ToggleButton>
                                                <Button.Or />
                                                <ToggleButton variant="secondary" size='lg' value={'rabbit'}>Rabbit</ToggleButton>
                                            </ToggleButtonGroup>
                                        </Button.Group>
                                    </Segment>
                                </Sticky>
                            </Rail>
                        </Container>
                    </Ref>
                </Grid.Column>
            </Grid>
        </Container>
    );
};

export default SearchTool;
