import React from 'react'
import {
    ModalHeader,
    ModalDescription,
    ModalContent,
    ModalActions,
    Button,
    Header,
    Image,
    Modal,
} from 'semantic-ui-react'

function DetailsModal({ toggleOpen, animalData, isOpen }) {
    const { name, age, breeds, description, attributes, photos, contact, colors, status, tags } = animalData || {}
    return (
        <Modal
            onClose={toggleOpen}
            onOpen={toggleOpen}
            open={isOpen}
            size="small"
            centered={false}
            style={{ top: '0%', left: '27%', height: 'fit-content' }}
            closeIcon={true}
        >
            <ModalHeader>Learn more about {name} </ModalHeader>
            <ModalContent image>
                <Image size='medium' src={photos?.['medium']} wrapped />
                <ModalDescription>
                    <Header> </Header>
                    <p>
                        {description}
                    </p>

                </ModalDescription>
            </ModalContent>
            <ModalActions>
                <Button color='black' onClick={() => toggleOpen(false)}>
                    Done
                </Button>

            </ModalActions>
        </Modal >
    )
}

export default DetailsModal
