import React, { useState, useEffect, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Container, ButtonToolbar, ToggleButton, ToggleButtonGroup
} from 'react-bootstrap';
import {
    Form, Input, Icon, Button, Pagination, Grid, Ref, Segment, Sticky, Rail
} from 'semantic-ui-react';
import Expand from './Expand';
import GetData from './GetData';
import { boundChangePetData } from '../Redux/actions';
const PETFINDER_CLIENT_ID = "z75x8asWGx4VqYVYbTjjGmrbGKCyd8euVj0YJxQPDSXT5lfnvv";
const PETFINDER_CLIENT_SECRET = "CurSmxCxNgci2vHdbeZlPYi3hlAsnxZyaaZNh3Ny";
const tokenUrl = "https://api.petfinder.com/v2/oauth2/token";
const apiUrl = "http://localhost:5000/api/animals";

let token = "";
let tokenExpiry = 0;



function SearchTool() {
    const contextRef = useRef();

    const [state, setState] = useState({
        petType: 'Dog',
        page: 1,
        location: '94112',
        sort: '-recent',
        coat: '',
        age: '',
        good_with_children: '',
        good_with_dogs: '',
        good_with_cats: '',
        input: '',
        limit: '20',
        items: [],
        totalPageCount: '',
        currentPage: '',
        isloading: null,
        isLoaded: false,
        empty: '',
        loadingMessage: 'Loading...'
    });

    const tokenRef = useRef('');

    // useEffect(() => {
    //     (async () => {
    //         await getToken();
    //         await requestData();
    //     })();
    // }, []);

    async function fetchAccessToken() {
        const res = await fetch(tokenUrl, {
            method: "POST",
            headers: { "Content-Type": "application/x-www-form-urlencoded" },
            body: `grant_type=client_credentials&client_id=${PETFINDER_CLIENT_ID}&client_secret=${PETFINDER_CLIENT_SECRET}`
        });

        const json = await res.json();
        token = json.access_token;
        tokenExpiry = Date.now() + json.expires_in * 1000;
    }

    async function fetchAnimals(params) {
        // Refresh token if expired
        if (!token || Date.now() >= tokenExpiry) {
            await fetchAccessToken();
        }

        const query = new URLSearchParams(params).toString();
        const res = await fetch(`${apiUrl}?${query}`, {
            headers: {
                Authorization: `Bearer ${token}`
            }
        });

        if (res.status === 401) {
            // Token might have expired unexpectedly; retry once
            await fetchAccessToken();
            return await fetchAnimals(params);
        }

        const data = await res.json();
        return data;
    }

    useEffect(() => {
        const loadAnimals = async () => {
            const data = await fetchAnimals({
                type: 'Dog',
                page: 1,
                location: '94112',
                sort: '-recent',
                limit: 20
            });
            setState(prev => ({
                ...prev,
                items: data.animals || [],
                isLoaded: true,
                empty: !data.animals?.length
                    ? 'No pets found in your area right now. Check back soon!'
                    : '',
                totalPageCount: data.pagination?.total_pages || '',
                currentPage: data.pagination?.current_page || ''
            }));
            console.log(data);
        };


        loadAnimals();
    }, []);

    const getToken = async () => {
        const res = await fetch(tokenUrl, {
            method: 'POST',
            headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
            body: `grant_type=client_credentials&client_id=${PETFINDER_CLIENT_ID}&client_secret=${PETFINDER_CLIENT_SECRET}`
        });
        const data = await res.json();
        tokenRef.current = data.access_token;
    };

    const requestData = async () => {
        const params = {
            type: state.petType,
            page: state.page,
            location: state.location,
            sort: state.sort,
            age: state.age,
            good_with_children: state.good_with_children,
            good_with_dogs: state.good_with_dogs,
            good_with_cats: state.good_with_cats,
            coat: state.coat,
            limit: state.limit
        };

        const queryString = Object.entries(params)
            .filter(([_, val]) => val !== '')
            .map(([key, val]) => `${key}=${val}`)
            .join('&');

        const res = await fetch(`${apiUrl}?${queryString}`, {
            headers: {
                Authorization: `Bearer ${tokenRef.current}`,
                'Content-Type': 'application/json'
            }
        });

        const data = await res.json();
        setState(prev => ({
            ...prev,
            items: data.animals || [],
            isLoaded: true,
            empty: !data.animals?.length
                ? 'No pets found in your area right now. Check back soon!'
                : '',
            totalPageCount: data.pagination?.total_pages || '',
            currentPage: data.pagination?.current_page || ''
        }));

        boundChangePetData(data.animals || []);
    };

    const updateStateAndFetch = async (changes) => {
        setState(prev => ({ ...prev, ...changes, isloading: true }));
        await getToken();
        await requestData();
        setState(prev => ({ ...prev, isloading: false }));
    };

    const toggleCheckbox = (key) => updateStateAndFetch({
        [key]: state[key] === '' ? true : !state[key]
    });

    const handleChangePetType = val => updateStateAndFetch({ petType: val });

    const handleChangePetAge = val => {
        const newVal = state.age ? `${state.age},${val}` : val;
        updateStateAndFetch({ age: newVal });
    };

    const handleChangeCoat = val => {
        const newVal = state.coat ? `${state.coat},${val}` : val;
        updateStateAndFetch({ coat: newVal });
    };

    const handlePagination = async (_, data) => {
        await updateStateAndFetch({ page: data.activePage });
    };

    const onSearchInput = e => setState(prev => ({ ...prev, input: e.target.value }));

    const onSearchSubmit = async e => {
        e.preventDefault();
        await updateStateAndFetch({ location: state.input, input: '' });
    };

    return (
        <Container className="dataBox" style={{ minHeight: '80vh', display: 'flex', flexDirection: 'row', width: '100vw', maxWidth: '100%', overflow: 'hidden' }}>
            <Grid style={{ width: '100%', margin: 0 }}>
                <Grid.Row style={{ display: 'flex', flexWrap: 'nowrap', margin: 0 }}>
                    <Grid.Column width={5} style={{ paddingRight: '2rem', minWidth: 0 }}>
                        <Ref innerRef={contextRef}>
                            <Container style={{ minHeight: '70vh', padding: '2rem', background: '#fff', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.05)', width: '100%', overflowX: 'auto' }}>
                                {state.empty && <div>{state.empty}</div>}
                                <GetData items={state.items} isLoaded={state.isLoaded} />
                                <Pagination
                                    onPageChange={handlePagination}
                                    activePage={state.page}
                                    totalPages={state.totalPageCount}
                                    style={{ marginTop: '2rem', textAlign: 'center' }}
                                />
                            </Container>
                        </Ref>
                    </Grid.Column>

                    <Grid.Column width={3} style={{ paddingLeft: '2rem', minWidth: '340px', maxWidth: '340px', flex: '0 0 340px' }}>
                        <Rail position="right">
                            <Sticky context={contextRef} offset={0} style={{ top: '0', right: '0', width: '340px', minHeight: '100vh', zIndex: 1000, position: 'sticky', alignItems: 'flex-start', display: 'flex', flexDirection: 'column' }}>
                                <Segment style={{ width: '100%', minHeight: '100vh', background: '#f9f9f9', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.05)', padding: '2rem', display: 'flex', flexDirection: 'column', gap: '1.5rem', overflowY: 'auto' }}>
                                    <Form onSubmit={onSearchSubmit} style={{ marginBottom: '1rem' }}>
                                        <Form.Field>
                                            <label>ZIP Code</label>
                                            <Input
                                                value={state.input}
                                                onChange={onSearchInput}
                                                icon={<Icon name="search" circular link onClick={onSearchSubmit} />}
                                            />
                                        </Form.Field>
                                    </Form>

                                    <div>Species</div>
                                    <ButtonToolbar style={{ marginBottom: '1rem' }}>
                                        <ToggleButtonGroup type="radio" name="species" value={state.petType} onChange={handleChangePetType}>
                                            <ToggleButton value="Dog">Dog</ToggleButton>
                                            <ToggleButton value="Cat">Cat</ToggleButton>
                                            <ToggleButton value="Rabbit">Rabbit</ToggleButton>
                                        </ToggleButtonGroup>
                                    </ButtonToolbar>

                                    <div>Age</div>
                                    <ButtonToolbar style={{ marginBottom: '1rem' }}>
                                        <ToggleButtonGroup type="checkbox" onChange={handleChangePetAge}>
                                            <ToggleButton value="baby">Baby</ToggleButton>
                                            <ToggleButton value="young">Young</ToggleButton>
                                            <ToggleButton value="adult">Adult</ToggleButton>
                                            <ToggleButton value="senior">Senior</ToggleButton>
                                        </ToggleButtonGroup>
                                    </ButtonToolbar>

                                    <div>Environment</div>
                                    <ButtonToolbar style={{ marginBottom: '1rem' }}>
                                        <Button onClick={() => toggleCheckbox("good_with_cats")} color={state.good_with_cats ? 'blue' : null}>Cats</Button>
                                        <Button onClick={() => toggleCheckbox("good_with_dogs")} color={state.good_with_dogs ? 'blue' : null}>Dogs</Button>
                                        <Button onClick={() => toggleCheckbox("good_with_children")} color={state.good_with_children ? 'blue' : null}>Children</Button>
                                    </ButtonToolbar>

                                    <div>Coat</div>
                                    <ButtonToolbar style={{ marginBottom: '1rem' }}>
                                        <ToggleButtonGroup type="checkbox" onChange={handleChangeCoat}>
                                            <ToggleButton value="short">Short</ToggleButton>
                                            <ToggleButton value="medium">Medium</ToggleButton>
                                            <ToggleButton value="long">Long</ToggleButton>
                                            <ToggleButton value="wire">Wire</ToggleButton>
                                            <ToggleButton value="hairless">Hairless</ToggleButton>
                                            <ToggleButton value="curly">Curly</ToggleButton>
                                        </ToggleButtonGroup>
                                    </ButtonToolbar>

                                    <Expand />
                                </Segment>
                            </Sticky>
                        </Rail>
                    </Grid.Column>
                </Grid.Row>
            </Grid>
        </Container>
    );
}

export default SearchTool
