import React, { useEffect, useState, useRef } from 'react';
import { Container, ButtonToolbar, ToggleButton, ToggleButtonGroup } from 'react-bootstrap';
import { Form, Input, Icon, Pagination } from 'semantic-ui-react';
import { Grid, Rail, Ref, Segment, Sticky } from 'semantic-ui-react';
import GetData from './GetData';
import { boundChangePetData } from '../Redux/actions';

const apiBase = "http://localhost:5000";

const SearchTool = () => {
    const contextRef = useRef();
    const [state, setState] = useState({
        petType: 'Dog',
        page: 1,
        location: '94112',
        sort: '-recent',
        age: '',
        coat: '',
        good_with_cats: '',
        good_with_dogs: '',
        good_with_children: '',
        input: '',
        items: [],
        isLoaded: false,
        totalPageCount: '',
        currentPage: '',
        empty: ''
    });

    const fetchAnimals = async () => {
        const queryParams = new URLSearchParams({
            type: state.petType,
            location: state.location,
            page: state.page,
            sort: state.sort,
            age: state.age,
            coat: state.coat,
            good_with_cats: state.good_with_cats,
            good_with_dogs: state.good_with_dogs,
            good_with_children: state.good_with_children,
            limit: 20
        });

        const res = await fetch(`${apiBase}/api/animals?${queryParams.toString()}`);
        const data = await res.json();

        boundChangePetData(data.animals || []);
        setState(prev => ({
            ...prev,
            items: data.animals || [],
            isLoaded: true,
            empty: data.animals?.length === 0 ? 'Sorry! No results.' : '',
            totalPageCount: data.pagination?.total_pages,
            currentPage: data.pagination?.current_page
        }));
    };

    useEffect(() => {
        fetchAnimals();
    }, []);

    const onChangeCity = (e) => setState(prev => ({ ...prev, input: e.target.value }));

    const onSubmitCity = async (e) => {
        e.preventDefault();
        setState(prev => ({ ...prev, location: prev.input, page: 1, input: '' }));
        await fetchAnimals();
    };

    const onSpeciesChange = async (val) => {
        setState(prev => ({ ...prev, petType: val, page: 1 }));
        await fetchAnimals();
    };

    const onPageChange = async (e, { activePage }) => {
        setState(prev => ({ ...prev, page: activePage }));
        await fetchAnimals();
    };

    return (
        <Container>
            <Grid>
                <Grid.Column columns={2}>
                    <Ref innerRef={contextRef}>
                        <Container>
                            <Sticky>
                                <Pagination onPageChange={onPageChange} defaultActivePage={state.page} totalPages={state.totalPageCount} />
                            </Sticky>
                            <div>{state.empty}</div>
                            <GetData items={state.items} isLoaded={state.isLoaded} />
                            <Rail>
                                <Sticky>
                                    <Segment>
                                        <Form onSubmit={onSubmitCity}>
                                            <Form.Field>
                                                <Input
                                                    placeholder="ZIP..."
                                                    value={state.input}
                                                    onChange={onChangeCity}
                                                    icon={<Icon name='search' link onClick={onSubmitCity} />}
                                                />
                                            </Form.Field>
                                        </Form>
                                        <div>Species</div>
                                        <ButtonToolbar>
                                            <ToggleButtonGroup type="radio" onChange={onSpeciesChange}>
                                                <ToggleButton value="dog">Dog</ToggleButton>
                                                <ToggleButton value="cat">Cat</ToggleButton>
                                                <ToggleButton value="rabbit">Rabbit</ToggleButton>
                                            </ToggleButtonGroup>
                                        </ButtonToolbar>
                                    </Segment>
                                </Sticky>
                            </Rail>
                        </Container>
                    </Ref>
                </Grid.Column>
            </Grid>
        </Container>
    );
};

export default SearchTool;
